mapper.select:
SELECT 

FROM act_delegate a

WHERE a.id = #{id}

SELECT 

FROM act_delegate a

where
	a.canceled = '0'
	AND a.assign = #{assign}
	AND a.start_date <= #{nowDate}
	AND a.end_date >= #{nowDate}

SELECT 

FROM act_delegate a




		AND a.owner = #{owner}


		AND a.assign = #{assign}


		AND a.process_id = #{processId}


		AND a.end_date >= #{startDate}


		AND a.start_date <= #{endDate}


		AND a.canceled = #{canceled}




		ORDER BY ${page.orderBy}

SELECT 

FROM act_delegate a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE act_delegate SET 	
	start_date = #{startDate},
	end_date = #{endDate},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE act_delegate SET 
canceled = 1,
cancel_date = #{cancelDate}
WHERE id = #{id}
